DeviceProperties.ReadLevel:
var ret := MODBUSR(H, 14, Int16) / 1000;
counter := counter - 1;
if counter <= 0 or ret = Le
    return(ret);

else
    return(LE);
end

var ret := MODBUSR(H, 13, Int16) /1000;
counter := counter - 1;
if counter <= 0 or ret = Le
    return(ret);

else
    return(LE);
end

DeviceProperties.ReadDesiredTemperature:
var ret := MODBUSR(H, 11, Int16) * 0.1;
counter := counter - 1;
if counter <= 0 or ret = Se
    return(ret);

else
    return(Se);
end

DeviceProperties.ReadSwitchState:
var ret := MODBUSR(H, 215, Int16);
counter := counter - 1;
if counter <= 0 or ret = Mu
    return(ret);

else
    return(Mu);
end
